// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IFdcVerification.sol";
import "./interfaces/IFdcHub.sol";
import "./interfaces/IFlareContractRegistry.sol";

/**
 * @title FDCVerifier
 * @notice Verifies Flare Data Connector attestation proofs for external data.
 *
 *         Primary use-case: escrow release is gated on an FDC-attested
 *         "delivery status" from a Web2 API — making the release trustless.
 *
 *         The contract stores verified delivery confirmations per job ID,
 *         which FlareEscrow checks before releasing funds.
 */
contract FDCVerifier is Ownable {
    /// @dev Flare Contract Registry (same address on Coston2 & mainnet)
    address public constant FLARE_CONTRACT_REGISTRY =
        0xaD67FE66660Fb8dFE9d6b1b4240d8650e30F6019;

    /// @dev Attestation type hash for JSON-API requests
    bytes32 public constant JSON_API_ATTESTATION_TYPE =
        keccak256("IJsonApi");

    /// @dev FDC verification contract (resolved from registry or set manually)
    IFdcVerification public fdcVerification;

    /// @dev FDC Hub for requesting attestations
    IFdcHub public fdcHub;

    /// @dev jobId => whether delivery has been attested as complete
    mapping(uint256 => bool) public deliveryConfirmed;

    /// @dev jobId => attestation proof hash (for audit trail)
    mapping(uint256 => bytes32) public attestationHashes;

    /// @dev Authorised callers that can submit attestation proofs (e.g. agents)
    mapping(address => bool) public authorisedSubmitters;

    event FdcVerificationSet(address indexed verifier);
    event FdcHubSet(address indexed hub);
    event DeliveryAttested(
        uint256 indexed jobId,
        bytes32 proofHash,
        address indexed submitter
    );
    event SubmitterAuthorised(address indexed submitter, bool authorised);

    modifier onlyAuthorised() {
        require(
            authorisedSubmitters[msg.sender] || msg.sender == owner(),
            "FDCVerifier: not authorised"
        );
        _;
    }

    constructor(address initialOwner) Ownable(initialOwner) {
        _resolveFdcContracts();
    }

    // ─── Configuration ──────────────────────────────────────

    function resolveFdcContracts() external onlyOwner {
        _resolveFdcContracts();
    }

    function setFdcVerification(address verifier) external onlyOwner {
        fdcVerification = IFdcVerification(verifier);
        emit FdcVerificationSet(verifier);
    }

    function setFdcHub(address hub) external onlyOwner {
        fdcHub = IFdcHub(hub);
        emit FdcHubSet(hub);
    }

    function setSubmitterAuthorised(
        address submitter,
        bool authorised
    ) external onlyOwner {
        authorisedSubmitters[submitter] = authorised;
        emit SubmitterAuthorised(submitter, authorised);
    }

    // ─── Attestation Request ────────────────────────────────

    /**
     * @notice Request an FDC attestation for a job's delivery status.
     * @param attestationData  ABI-encoded attestation request
     *                         (built off-chain by the agent).
     * @return requestId  The FDC request identifier.
     */
    function requestDeliveryAttestation(
        bytes calldata attestationData
    ) external payable onlyAuthorised returns (bytes32 requestId) {
        require(address(fdcHub) != address(0), "FDCVerifier: hub not set");
        requestId = fdcHub.requestAttestation{value: msg.value}(
            attestationData
        );
    }

    // ─── Attestation Verification ───────────────────────────

    /**
     * @notice Submit and verify an FDC proof that delivery is complete.
     *         The proof's response body must ABI-encode to (uint256 jobId, bool delivered).
     *
     * @dev This is the KEY integration point for the Flare BONUS bounty:
     *      escrow release is driven entirely by FDC-attested external data,
     *      not by a trusted backend.
     *
     * @param jobId  The on-chain job ID.
     * @param proof  The FDC Merkle proof for the Web2 JSON API attestation.
     */
    function verifyDelivery(
        uint256 jobId,
        IJsonApi.Proof calldata proof
    ) external onlyAuthorised {
        require(!deliveryConfirmed[jobId], "FDCVerifier: already confirmed");
        require(
            address(fdcVerification) != address(0),
            "FDCVerifier: verifier not set"
        );

        // Verify the Merkle proof against the FDC relay
        bool valid = fdcVerification.verifyJsonApi(proof);
        require(valid, "FDCVerifier: invalid proof");

        // Decode the response: expect (uint256 _jobId, bool _delivered)
        (uint256 attestedJobId, bool delivered) = abi.decode(
            proof.body.responseBody.abiEncodedData,
            (uint256, bool)
        );
        require(attestedJobId == jobId, "FDCVerifier: job ID mismatch");
        require(delivered, "FDCVerifier: delivery not confirmed");

        deliveryConfirmed[jobId] = true;
        attestationHashes[jobId] = keccak256(abi.encode(proof));

        emit DeliveryAttested(
            jobId,
            attestationHashes[jobId],
            msg.sender
        );
    }

    /**
     * @notice For local/test environments: manually confirm delivery.
     *         This bypasses FDC verification. ONLY available to the owner.
     */
    function manualConfirmDelivery(uint256 jobId) external onlyOwner {
        require(!deliveryConfirmed[jobId], "FDCVerifier: already confirmed");
        deliveryConfirmed[jobId] = true;
        attestationHashes[jobId] = bytes32(0);
        emit DeliveryAttested(jobId, bytes32(0), msg.sender);
    }

    // ─── View ───────────────────────────────────────────────

    function isDeliveryConfirmed(uint256 jobId) external view returns (bool) {
        return deliveryConfirmed[jobId];
    }

    // ─── Internals ──────────────────────────────────────────

    function _resolveFdcContracts() internal {
        if (FLARE_CONTRACT_REGISTRY.code.length == 0) return;

        IFlareContractRegistry registry = IFlareContractRegistry(
            FLARE_CONTRACT_REGISTRY
        );

        address verifier = registry.getContractAddressByName(
            "FdcVerification"
        );
        if (verifier != address(0)) {
            fdcVerification = IFdcVerification(verifier);
            emit FdcVerificationSet(verifier);
        }

        address hub = registry.getContractAddressByName("FdcHub");
        if (hub != address(0)) {
            fdcHub = IFdcHub(hub);
            emit FdcHubSet(hub);
        }
    }
}

contracts/FlareEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./FTSOPriceConsumer.sol";

/**
 * @title IFDCVerifier
 * @notice Interface for the FDCVerifier contract's delivery check.
 */
interface IFDCVerifier {
    function isDeliveryConfirmed(uint256 jobId) external view returns (bool);
}

/**
 * @title IFlareOrderBook
 * @notice Callback interface so escrow can update job status.
 */
interface IFlareOrderBook {
    function markReleased(uint256 jobId) external;
}

/**
 * @title FlareEscrow
 * @notice Holds native FLR or ERC-20 stablecoins (Plasma-bridged USDC) for jobs.
 *         Validates funding against FTSO prices.
 *         Release is gated on FDC-attested delivery confirmation.
 *
 *         Key integrations:
 *         - FTSO: fundJob() validates that value ≥ FTSO-derived amount
 *         - FDC:  releaseToProvider() requires FDCVerifier.isDeliveryConfirmed()
 *         - Plasma: supports ERC-20 stablecoin deposits for secure payments
 *
 *         This satisfies Flare MAIN (FTSO + FDC), Flare BONUS (FDC-driven
 *         escrow release), and Plasma bonus (stablecoin transactions).
 */
contract FlareEscrow is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ─── Types ──────────────────────────────────────────────

    enum PaymentType { NATIVE_FLR, ERC20_STABLECOIN }

    struct Deposit {
        address poster;
        address provider;
        uint256 amount;         // amount locked (FLR wei or token base units)
        uint256 usdValue;       // USD equivalent at fund time (18 dec)
        PaymentType paymentType;
        address token;          // address(0) for native FLR, ERC-20 address for stablecoin
        bool funded;
        bool released;
        bool refunded;
    }

    // ─── State ──────────────────────────────────────────────

    mapping(uint256 => Deposit) public deposits;
    mapping(address => bool) public allowedStablecoins;  // Plasma-bridged USDC, etc.

    FTSOPriceConsumer public ftso;
    IFDCVerifier public fdcVerifier;
    IFlareOrderBook public orderBook;

    address public feeCollector;
    uint96 public platformFeeBps;       // e.g. 200 = 2%

    /// @dev Allow a small slippage buffer (5%) to handle price movement
    ///      between the quote on the front-end and the on-chain fundJob() tx.
    uint256 public slippageBps = 500;   // 5%

    // ─── Events ─────────────────────────────────────────────

    event EscrowFunded(
        uint256 indexed jobId,
        address indexed poster,
        address indexed provider,
        uint256 amount,
        uint256 amountUsd,
        PaymentType paymentType,
        address token
    );
    event PaymentReleased(
        uint256 indexed jobId,
        address indexed provider,
        uint256 payout,
        uint256 fee,
        PaymentType paymentType
    );
    event PaymentRefunded(
        uint256 indexed jobId,
        address indexed poster,
        uint256 amount,
        PaymentType paymentType
    );
    event StablecoinUpdated(address indexed token, bool allowed);

    // ─── Constructor ────────────────────────────────────────

    constructor(
        address initialOwner,
        address ftsoConsumer,
        address feeCollector_
    ) Ownable(initialOwner) {
        ftso = FTSOPriceConsumer(ftsoConsumer);
        feeCollector = feeCollector_;
        platformFeeBps = 200; // 2%
    }

    // ─── Config ─────────────────────────────────────────────

    function setOrderBook(address orderBook_) external onlyOwner {
        orderBook = IFlareOrderBook(orderBook_);
    }

    function setFdcVerifier(address verifier) external onlyOwner {
        fdcVerifier = IFDCVerifier(verifier);
    }

    function setFTSO(address ftsoConsumer) external onlyOwner {
        ftso = FTSOPriceConsumer(ftsoConsumer);
    }

    function setFeeCollector(
        address collector,
        uint96 feeBps
    ) external onlyOwner {
        require(feeBps <= 1_000, "FlareEscrow: fee too high"); // max 10%
        feeCollector = collector;
        platformFeeBps = feeBps;
    }

    function setSlippageBps(uint256 bps) external onlyOwner {
        require(bps <= 2_000, "FlareEscrow: slippage too high"); // max 20%
        slippageBps = bps;
    }

    /**
     * @notice Whitelist or delist a stablecoin (e.g. Plasma-bridged USDC).
     */
    function setAllowedStablecoin(address token, bool allowed) external onlyOwner {
        require(token != address(0), "FlareEscrow: zero address");
        allowedStablecoins[token] = allowed;
        emit StablecoinUpdated(token, allowed);
    }

    // ─── Core Functions ─────────────────────────────────────

    /**
     * @notice Fund a job's escrow with native FLR.
     *
     *         ── FTSO integration (Flare MAIN) ──
     *         The contract reads the live FLR/USD FTSO price and validates that
     *         the sent FLR value covers the job's USD budget (with slippage).
     */
    function fundJob(
        uint256 jobId,
        address provider,
        uint256 usdBudget
    ) external payable {
        Deposit storage dep = deposits[jobId];
        require(!dep.funded, "FlareEscrow: already funded");
        require(msg.value > 0, "FlareEscrow: zero value");
        require(provider != address(0), "FlareEscrow: zero provider");

        // ── FTSO price validation ──
        uint256 requiredFlr = ftso.usdToFlr(usdBudget);
        uint256 minRequired = (requiredFlr * (10_000 - slippageBps)) / 10_000;
        require(
            msg.value >= minRequired,
            "FlareEscrow: insufficient FLR for USD budget"
        );

        dep.poster = msg.sender;
        dep.provider = provider;
        dep.amount = msg.value;
        dep.usdValue = usdBudget;
        dep.paymentType = PaymentType.NATIVE_FLR;
        dep.token = address(0);
        dep.funded = true;

        emit EscrowFunded(jobId, msg.sender, provider, msg.value, usdBudget, PaymentType.NATIVE_FLR, address(0));
    }

    /**
     * @notice Fund a job's escrow with an ERC-20 stablecoin (Plasma-bridged USDC).
     *
     *         ── Plasma Bonus Track ──
     *         Supports secure stablecoin transactions via Plasma infrastructure.
     *         The token must be whitelisted via setAllowedStablecoin().
     *         Caller must approve this contract first.
     *
     * @param jobId       The FlareOrderBook job ID
     * @param provider    The assigned agent's address
     * @param token       The stablecoin token address (e.g. Plasma-bridged USDC)
     * @param amount      The token amount in base units (e.g. 50e6 for 50 USDC)
     * @param usdBudget   The USD value (18 decimals) for accounting
     */
    function fundJobWithStablecoin(
        uint256 jobId,
        address provider,
        address token,
        uint256 amount,
        uint256 usdBudget
    ) external {
        Deposit storage dep = deposits[jobId];
        require(!dep.funded, "FlareEscrow: already funded");
        require(amount > 0, "FlareEscrow: zero amount");
        require(provider != address(0), "FlareEscrow: zero provider");
        require(allowedStablecoins[token], "FlareEscrow: token not allowed");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        dep.poster = msg.sender;
        dep.provider = provider;
        dep.amount = amount;
        dep.usdValue = usdBudget;
        dep.paymentType = PaymentType.ERC20_STABLECOIN;
        dep.token = token;
        dep.funded = true;

        emit EscrowFunded(jobId, msg.sender, provider, amount, usdBudget, PaymentType.ERC20_STABLECOIN, token);
    }

    /**
     * @notice Release payment to the provider.
     *
     *         ── FDC integration (Flare MAIN + BONUS) ──
     *         This function REQUIRES that the FDCVerifier has confirmed delivery
     *         via an FDC-attested external data proof. The escrow release is
     *         driven entirely by FDC, not by a trusted backend.
     *
     *         Supports both native FLR and ERC-20 stablecoin payouts.
     */
    function releaseToProvider(uint256 jobId) external nonReentrant {
        Deposit storage dep = deposits[jobId];
        require(dep.funded, "FlareEscrow: not funded");
        require(!dep.released, "FlareEscrow: already released");
        require(!dep.refunded, "FlareEscrow: refunded");

        // ── FDC gate ──
        require(
            address(fdcVerifier) != address(0),
            "FlareEscrow: FDC verifier not set"
        );
        require(
            fdcVerifier.isDeliveryConfirmed(jobId),
            "FlareEscrow: delivery not attested by FDC"
        );

        // Caller must be poster or provider
        require(
            msg.sender == dep.poster || msg.sender == dep.provider,
            "FlareEscrow: not authorised"
        );

        dep.released = true;

        uint256 fee = (dep.amount * platformFeeBps) / 10_000;
        uint256 payout = dep.amount - fee;

        if (dep.paymentType == PaymentType.NATIVE_FLR) {
            if (fee > 0 && feeCollector != address(0)) {
                (bool feeOk, ) = feeCollector.call{value: fee}("");
                require(feeOk, "FlareEscrow: fee transfer failed");
            }
            (bool ok, ) = dep.provider.call{value: payout}("");
            require(ok, "FlareEscrow: payout failed");
        } else {
            IERC20 token = IERC20(dep.token);
            if (fee > 0 && feeCollector != address(0)) {
                token.safeTransfer(feeCollector, fee);
            }
            token.safeTransfer(dep.provider, payout);
        }

        // Update OrderBook status
        if (address(orderBook) != address(0)) {
            orderBook.markReleased(jobId);
        }

        emit PaymentReleased(jobId, dep.provider, payout, fee, dep.paymentType);
    }

    /**
     * @notice Refund the poster. Can only be called by the owner (dispute resolution).
     *         Supports both native FLR and ERC-20 stablecoin refunds.
     */
    function refund(uint256 jobId) external nonReentrant {
        Deposit storage dep = deposits[jobId];
        require(dep.funded, "FlareEscrow: not funded");
        require(!dep.released, "FlareEscrow: already released");
        require(!dep.refunded, "FlareEscrow: already refunded");

        // Only owner can refund (dispute resolution)
        require(
            msg.sender == owner(),
            "FlareEscrow: not authorised"
        );

        dep.refunded = true;

        if (dep.paymentType == PaymentType.NATIVE_FLR) {
            (bool ok, ) = dep.poster.call{value: dep.amount}("");
            require(ok, "FlareEscrow: refund failed");
        } else {
            IERC20(dep.token).safeTransfer(dep.poster, dep.amount);
        }

        emit PaymentRefunded(jobId, dep.poster, dep.amount, dep.paymentType);
    }

    // ─── Views ──────────────────────────────────────────────

    function getDeposit(
        uint256 jobId
    ) external view returns (Deposit memory) {
        return deposits[jobId];
    }

    function isStablecoinAllowed(address token) external view returns (bool) {
        return allowedStablecoins[token];
    }

    /// @dev Accept native FLR
    receive() external payable {}
}

contracts/FlareOrderBook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./FTSOPriceConsumer.sol";

/**
 * @title FlareOrderBook
 * @notice Job lifecycle with competitive bidding for the SOTA Flare marketplace.
 *
 *         Workflow:
 *           1. createJob()      — poster describes work + max USD budget (FTSO-priced)
 *           2. placeBid()       — agents bid with a price ≤ budget
 *           3. acceptBid()      — poster selects the winning bid → assigns provider
 *           4. markCompleted()  — agent declares work done
 *           5. markReleased()   — called by FlareEscrow after FDC-gated release
 *
 *         Price conversion uses FTSO (Flare MAIN track requirement).
 *         Escrow release is gated by FDC (handled in FlareEscrow).
 */
contract FlareOrderBook is Ownable {
    // ─── Types ──────────────────────────────────────────────

    enum JobStatus {
        OPEN,
        ASSIGNED,
        COMPLETED,
        RELEASED,
        CANCELLED,
        DISPUTED
    }

    struct Job {
        uint256 id;
        address poster;
        address provider;           // assigned agent (after acceptBid)
        string  metadataURI;        // IPFS / off-chain job description
        uint256 maxPriceUsd;        // budget in USD (18 decimals)
        uint256 maxPriceFlr;        // FTSO-derived FLR equivalent at creation
        uint64  deadline;
        JobStatus status;
        bytes32 deliveryProof;
        uint256 createdAt;
        uint256 acceptedBidId;      // winning bid ID
    }

    struct Bid {
        uint256 id;
        uint256 jobId;
        address agent;
        uint256 priceUsd;           // bid price in USD (18 decimals)
        uint256 priceFlr;           // FTSO-derived FLR at bid time
        uint256 estimatedTime;      // seconds to complete
        string  proposal;           // brief description of approach
        uint256 createdAt;
        bool    accepted;
    }

    // ─── State ──────────────────────────────────────────────

    uint256 private _nextJobId = 1;
    uint256 private _nextBidId = 1;
    mapping(uint256 => Job) public jobs;
    mapping(uint256 => Bid) public bids;
    mapping(uint256 => uint256[]) public jobBids;   // jobId → bidId[]
    uint256[] public jobIds;                         // for enumeration

    FTSOPriceConsumer public ftso;
    address public escrow;                           // FlareEscrow address

    // ─── Events ─────────────────────────────────────────────

    event JobCreated(
        uint256 indexed jobId,
        address indexed poster,
        uint256 maxPriceUsd,
        uint256 maxPriceFlr
    );
    event BidPlaced(
        uint256 indexed jobId,
        uint256 indexed bidId,
        address indexed agent,
        uint256 priceUsd,
        uint256 priceFlr
    );
    event BidAccepted(
        uint256 indexed jobId,
        uint256 indexed bidId,
        address indexed provider
    );
    event ProviderAssigned(
        uint256 indexed jobId,
        address indexed provider
    );
    event JobCompleted(
        uint256 indexed jobId,
        bytes32 deliveryProof
    );
    event JobReleased(uint256 indexed jobId);
    event JobCancelled(uint256 indexed jobId);
    event JobDisputed(uint256 indexed jobId, address indexed disputedBy);

    // ─── Constructor ────────────────────────────────────────

    constructor(
        address initialOwner,
        address ftsoConsumer
    ) Ownable(initialOwner) {
        ftso = FTSOPriceConsumer(ftsoConsumer);
    }

    // ─── Config ─────────────────────────────────────────────

    function setEscrow(address escrow_) external onlyOwner {
        escrow = escrow_;
    }

    function setFTSO(address ftsoConsumer) external onlyOwner {
        ftso = FTSOPriceConsumer(ftsoConsumer);
    }

    // ─── Core Functions ─────────────────────────────────────

    /**
     * @notice Post a new job with a max USD budget.
     *         FTSO is used to derive the FLR equivalent for on-chain pricing.
     */
    function createJob(
        string calldata metadataURI,
        uint256 maxPriceUsd,
        uint64 deadline
    ) external returns (uint256 jobId) {
        require(maxPriceUsd > 0, "FlareOrderBook: zero budget");
        require(deadline > block.timestamp, "FlareOrderBook: past deadline");

        // ── FTSO integration (Flare MAIN) ──
        uint256 flrEquivalent = ftso.usdToFlr(maxPriceUsd);
        require(flrEquivalent > 0, "FlareOrderBook: FTSO price error");

        jobId = _nextJobId++;

        jobs[jobId] = Job({
            id: jobId,
            poster: msg.sender,
            provider: address(0),
            metadataURI: metadataURI,
            maxPriceUsd: maxPriceUsd,
            maxPriceFlr: flrEquivalent,
            deadline: deadline,
            status: JobStatus.OPEN,
            deliveryProof: bytes32(0),
            createdAt: block.timestamp,
            acceptedBidId: 0
        });

        jobIds.push(jobId);
        emit JobCreated(jobId, msg.sender, maxPriceUsd, flrEquivalent);
    }

    /**
     * @notice Agent places a bid on an OPEN job.
     *         Bid price must be ≤ the job's max budget.
     *         FTSO converts the bid's USD price to FLR at bid time.
     */
    function placeBid(
        uint256 jobId,
        uint256 priceUsd,
        uint256 estimatedTime,
        string calldata proposal
    ) external returns (uint256 bidId) {
        Job storage job = jobs[jobId];
        require(job.status == JobStatus.OPEN, "FlareOrderBook: not open");
        require(block.timestamp < job.deadline, "FlareOrderBook: past deadline");
        require(priceUsd > 0, "FlareOrderBook: zero bid");
        require(priceUsd <= job.maxPriceUsd, "FlareOrderBook: bid exceeds budget");
        require(msg.sender != job.poster, "FlareOrderBook: poster cannot bid");

        uint256 flrEquivalent = ftso.usdToFlr(priceUsd);

        bidId = _nextBidId++;
        bids[bidId] = Bid({
            id: bidId,
            jobId: jobId,
            agent: msg.sender,
            priceUsd: priceUsd,
            priceFlr: flrEquivalent,
            estimatedTime: estimatedTime,
            proposal: proposal,
            createdAt: block.timestamp,
            accepted: false
        });

        jobBids[jobId].push(bidId);
        emit BidPlaced(jobId, bidId, msg.sender, priceUsd, flrEquivalent);
    }

    /**
     * @notice Poster accepts a bid and assigns the agent.
     *         After acceptance, the poster should call FlareEscrow.fundJob().
     */
    function acceptBid(uint256 jobId, uint256 bidId) external {
        Job storage job = jobs[jobId];
        require(job.poster == msg.sender, "FlareOrderBook: not poster");
        require(job.status == JobStatus.OPEN, "FlareOrderBook: not open");

        Bid storage bid = bids[bidId];
        require(bid.jobId == jobId, "FlareOrderBook: bid/job mismatch");
        require(!bid.accepted, "FlareOrderBook: bid already accepted");

        bid.accepted = true;
        job.provider = bid.agent;
        job.status = JobStatus.ASSIGNED;
        job.acceptedBidId = bidId;

        emit BidAccepted(jobId, bidId, bid.agent);
        emit ProviderAssigned(jobId, bid.agent);
    }

    /**
     * @notice Poster assigns an agent directly (without bidding).
     *         After assignment, the poster should call FlareEscrow.fundJob().
     */
    function assignProvider(
        uint256 jobId,
        address provider
    ) external {
        Job storage job = jobs[jobId];
        require(job.poster == msg.sender, "FlareOrderBook: not poster");
        require(job.status == JobStatus.OPEN, "FlareOrderBook: not open");
        require(provider != address(0), "FlareOrderBook: zero provider");

        job.provider = provider;
        job.status = JobStatus.ASSIGNED;

        emit ProviderAssigned(jobId, provider);
    }

    /**
     * @notice Assigned agent marks the job as completed.
     *         After this, the FDC proof must be submitted to FDCVerifier,
     *         then FlareEscrow.releaseToProvider() can be called.
     */
    function markCompleted(
        uint256 jobId,
        bytes32 deliveryProof
    ) external {
        Job storage job = jobs[jobId];
        require(job.provider == msg.sender, "FlareOrderBook: not provider");
        require(job.status == JobStatus.ASSIGNED, "FlareOrderBook: not assigned");

        job.status = JobStatus.COMPLETED;
        job.deliveryProof = deliveryProof;

        emit JobCompleted(jobId, deliveryProof);
    }

    /**
     * @notice Called by FlareEscrow after successful payment release.
     */
    function markReleased(uint256 jobId) external {
        require(msg.sender == escrow, "FlareOrderBook: not escrow");
        jobs[jobId].status = JobStatus.RELEASED;
        emit JobReleased(jobId);
    }

    /**
     * @notice Poster can cancel an OPEN job (before assignment).
     */
    function cancelJob(uint256 jobId) external {
        Job storage job = jobs[jobId];
        require(job.poster == msg.sender, "FlareOrderBook: not poster");
        require(job.status == JobStatus.OPEN, "FlareOrderBook: not open");
        job.status = JobStatus.CANCELLED;
        emit JobCancelled(jobId);
    }

    /**
     * @notice Poster or provider can raise a dispute on an ASSIGNED or COMPLETED job.
     */
    function raiseDispute(uint256 jobId) external {
        Job storage job = jobs[jobId];
        require(
            msg.sender == job.poster || msg.sender == job.provider,
            "FlareOrderBook: not party"
        );
        require(
            job.status == JobStatus.ASSIGNED || job.status == JobStatus.COMPLETED,
            "FlareOrderBook: cannot dispute"
        );
        job.status = JobStatus.DISPUTED;
        emit JobDisputed(jobId, msg.sender);
    }

    // ─── Views ──────────────────────────────────────────────

    /**
     * @notice Get a quote: how much FLR is needed for a given USD amount.
     */
    function quoteUsdToFlr(
        uint256 usdAmount
    ) external view returns (uint256 flrAmount) {
        flrAmount = ftso.usdToFlr(usdAmount);
    }

    function getJob(uint256 jobId) external view returns (Job memory) {
        return jobs[jobId];
    }

    function getBid(uint256 bidId) external view returns (Bid memory) {
        return bids[bidId];
    }

    function getJobBidIds(uint256 jobId) external view returns (uint256[] memory) {
        return jobBids[jobId];
    }

    function getJobBidCount(uint256 jobId) external view returns (uint256) {
        return jobBids[jobId].length;
    }

    function totalJobs() external view returns (uint256) {
        return jobIds.length;
    }

    function getJobIds(
        uint256 offset,
        uint256 limit
    ) external view returns (uint256[] memory ids) {
        uint256 total = jobIds.length;
        if (offset >= total) return new uint256[](0);
        uint256 end = offset + limit > total ? total : offset + limit;
        ids = new uint256[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            ids[i - offset] = jobIds[i];
        }
    }
}

contracts/FTSOPriceConsumer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IFastUpdater.sol";
import "./interfaces/IFlareContractRegistry.sol";

/**
 * @title FTSOPriceConsumer
 * @notice Wraps Flare's FTSO v2 Fast Updates to provide FLR/USD pricing.
 *         Used by FlareOrderBook and FlareEscrow to convert between USD and FLR.
 *
 *         On Coston2 the FlareContractRegistry lives at
 *         0xaD67FE66660Fb8dFE9d6b1b4240d8650e30F6019
 */
contract FTSOPriceConsumer is Ownable {
    /// @dev Well-known Flare Contract Registry address (same on Coston2 & mainnet)
    address public constant FLARE_CONTRACT_REGISTRY =
        0xaD67FE66660Fb8dFE9d6b1b4240d8650e30F6019;

    /// @dev Feed index for FLR/USD on Flare FTSO v2
    uint256 public constant FLR_USD_FEED_INDEX = 0;

    /// @dev Staleness threshold — reject prices older than this (seconds)
    uint256 public maxStaleness = 300; // 5 minutes

    /// @dev Cached FastUpdater address (resolved from registry)
    IFastUpdater public fastUpdater;

    event FastUpdaterSet(address indexed updater);
    event MaxStalenessUpdated(uint256 newStaleness);

    constructor(address initialOwner) Ownable(initialOwner) {
        // Try to resolve FastUpdater from the on-chain registry
        _resolveFastUpdater();
    }

    // ─── Configuration ──────────────────────────────────────

    /// @notice Re-resolve the FastUpdater address from Flare's contract registry
    function resolveFastUpdater() external onlyOwner {
        _resolveFastUpdater();
    }

    /// @notice Manually set FastUpdater (for local testing / non-Flare chains)
    function setFastUpdater(address updater) external onlyOwner {
        fastUpdater = IFastUpdater(updater);
        emit FastUpdaterSet(updater);
    }

    function setMaxStaleness(uint256 seconds_) external onlyOwner {
        maxStaleness = seconds_;
        emit MaxStalenessUpdated(seconds_);
    }

    // ─── Price Queries ──────────────────────────────────────

    /**
     * @notice Get the current FLR/USD price.
     * @return priceWei   The price of 1 FLR in USD, scaled to 18 decimals.
     * @return timestamp  When the price was last updated.
     */
    function getFlrUsdPrice()
        public
        view
        returns (uint256 priceWei, int64 timestamp)
    {
        require(address(fastUpdater) != address(0), "FTSO: updater not set");

        uint256[] memory indexes = new uint256[](1);
        indexes[0] = FLR_USD_FEED_INDEX;

        (
            uint256[] memory feeds,
            int8[] memory decimals,
            int64 ts
        ) = fastUpdater.fetchCurrentFeeds(indexes);

        require(feeds.length > 0, "FTSO: no feed data");
        require(
            block.timestamp - uint256(uint64(ts)) <= maxStaleness,
            "FTSO: price stale"
        );

        // Normalise to 18 decimals
        priceWei = _normalise(feeds[0], decimals[0]);
        timestamp = ts;
    }

    /**
     * @notice Convert a USD amount (18-decimal) to FLR (18-decimal wei).
     * @param usdAmount  USD value scaled to 18 decimals (e.g. 10 USD = 10e18)
     * @return flrAmount The equivalent amount of FLR in wei.
     */
    function usdToFlr(uint256 usdAmount)
        external
        view
        returns (uint256 flrAmount)
    {
        (uint256 priceWei, ) = getFlrUsdPrice();
        require(priceWei > 0, "FTSO: price is zero");
        // FLR = USD / (FLR per USD price)
        flrAmount = (usdAmount * 1e18) / priceWei;
    }

    /**
     * @notice Convert a FLR amount (wei) to USD (18-decimal).
     * @param flrAmount  FLR in wei
     * @return usdAmount USD value scaled to 18 decimals.
     */
    function flrToUsd(uint256 flrAmount)
        external
        view
        returns (uint256 usdAmount)
    {
        (uint256 priceWei, ) = getFlrUsdPrice();
        usdAmount = (flrAmount * priceWei) / 1e18;
    }

    // ─── Internals ──────────────────────────────────────────

    function _resolveFastUpdater() internal {
        // On non-Flare chains (local Hardhat), the registry won't exist
        if (FLARE_CONTRACT_REGISTRY.code.length == 0) return;

        address updater = IFlareContractRegistry(FLARE_CONTRACT_REGISTRY)
            .getContractAddressByName("FastUpdater");

        if (updater != address(0)) {
            fastUpdater = IFastUpdater(updater);
            emit FastUpdaterSet(updater);
        }
    }

    /**
     * @dev Normalise a feed value with its decimals to 18-decimal precision.
     */
    function _normalise(
        uint256 value,
        int8 feedDecimals
    ) internal pure returns (uint256) {
        if (feedDecimals >= 0) {
            uint8 d = uint8(int8(18) - feedDecimals);
            return value * (10 ** d);
        } else {
            uint8 d = uint8(int8(18) + feedDecimals); // feedDecimals is negative
            return value / (10 ** d);
        }
    }
}

contracts/interfaces/IFastUpdater.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title IFastUpdater
 * @notice Minimal interface for Flare's Fast Updates (FTSO v2 Scaling) price feeds.
 *         Provides sub-second price updates on Flare.
 */
interface IFastUpdater {
    /**
     * @notice Fetch current feed values by feed index array.
     * @param _feedIndexes Array of feed indexes (e.g. 0 = FLR/USD, 2 = BTC/USD, …)
     * @return _feeds       The current feed values (scaled integers)
     * @return _decimals    The number of decimals for each feed
     * @return _timestamp   Block timestamp of the update
     */
    function fetchCurrentFeeds(
        uint256[] calldata _feedIndexes
    )
        external
        view
        returns (
            uint256[] memory _feeds,
            int8[] memory _decimals,
            int64 _timestamp
        );
}

